import time


t, s = input(), open('wow.txt', 'r').readline()
before = time.time()
t += '~'
d = {}
length = len(t)
for i in range(length - 3, -1, -1):
    d[t[i]] = d.get(t[i], length - i - 2)
d[t[-1]] = length - 1
d[t[-2]] = d.get(t[-2], length - 1)
t = t[:-1]
elem = t[-1]
i = length - 2
k = 0
la = len(s)
while i < la:
    if s[i] != elem:
        i += d.get(s[i], d['~'])
    else:
        if s[i-length+2:i+1] == t:
            k = i-length+2
            break
        i += d[elem]
after = time.time()
print(k, after - before)

